vti_cachedtitle:SR|<?php /* Override the Title if the custom tag "_smallbiz_title" has been set. */ /* if "_smallbiz_extra_title" has been set, append this. */ $title = ""; $is_smallbiz_frontpage = is_front_page() || (get_option('smallbiz_page_on_front') == $wp_query->get_queried_object()->ID); if (is_singular() && !$is_smallbiz_frontpage){ if (get_post_meta($wp_query->get_queried_object()->ID, "_smallbiz_title", true) && get_post_meta($wp_query->get_queried_object()->ID, "_smallbiz_title", true) != ""){ ?><?php $title = get_post_meta($wp_query->get_queried_object()->ID, "_smallbiz_title", true); }else { $title = wp_title('|', true, 'right').biz_option('smallbiz_name'); } if(get_post_meta($wp_query->get_queried_object()->ID, "_smallbiz_extra_title", true)){ if($title != ""){ $title .= ","; } $title .= get_post_meta($wp_query->get_queried_object()->ID, "_smallbiz_extra_title", true); } } else if ($is_smallbiz_frontpage){ $title = biz_option('smallbiz_title'); } if(!$title){ $title = wp_title('|', true, 'right').biz_option('smallbiz_name');} echo $title; ?>
